// src/components/Login.js
import React, { useState, useEffect } from 'react';
import Button from '../ui/Button';
import logo from '../../assets/images/logo.png';
import clockimage from '../../assets/images/clock-image.png';
import { apiService } from '../../services/api';

const Login = () => {
    const [formData, setFormData] = useState({
        email: '',
        password: '',
        rememberMe: false
    });
    const [showPassword, setShowPassword] = useState(false);
    const [errors, setErrors] = useState({});
    const [isLoading, setIsLoading] = useState(false);
    const [backendStatus, setBackendStatus] = useState('checking');

    // Test backend connection on component mount
    useEffect(() => {
        const checkBackend = async () => {
            console.log('=== CHECKING BACKEND CONNECTION ===');
            const result = await apiService.testBackend();
            setBackendStatus(result.connected ? 'connected' : 'disconnected');

            if (result.connected) {
                console.log('✅ Backend is connected at:', result.workingUrl);
            } else {
                console.log('❌ Backend connection failed');
            }
        };
        checkBackend();
    }, []);

    const handleChange = (e) => {
        const value = e.target.type === 'checkbox' ? e.target.checked : e.target.value;
        setFormData({
            ...formData,
            [e.target.name]: value
        });
        if (errors[e.target.name]) {
            setErrors({
                ...errors,
                [e.target.name]: ''
            });
        }
        if (errors.general) {
            setErrors({
                ...errors,
                general: ''
            });
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        setErrors({});

        if (!formData.email || !formData.password) {
            setErrors({ general: 'Please fill in all fields' });
            setIsLoading(false);
            return;
        }

        try {
            const data = await apiService.login(formData.email, formData.password);
            console.log('✅ Login successful:', data);

            if (formData.rememberMe) {
                localStorage.setItem('rememberMe', 'true');
            }

            // Redirect to dashboard
            setTimeout(() => {
                window.location.href = '/dashboard';
            }, 1000);

        } catch (error) {
            console.error('❌ Login error:', error);

            if (error.status === 0) {
                setErrors({ general: 'Cannot connect to server. Please check the console for details.' });
            } else if (error.status === 401) {
                setErrors({ general: 'The account credentials are incorrect or it doesn\'t exist.' });
            } else if (error.status === 422) {
                // Handle validation errors
                const validationErrors = {};
                if (error.errors && Array.isArray(error.errors)) {
                    error.errors.forEach(err => {
                        if (err.loc && err.msg) {
                            const field = err.loc[err.loc.length - 1];
                            validationErrors[field] = err.msg;
                        }
                    });
                } else if (typeof error.errors === 'string') {
                    validationErrors.general = error.errors;
                }
                setErrors(validationErrors);
            } else {
                setErrors({ general: error.message || 'An error occurred during login' });
            }
        } finally {
            setIsLoading(false);
        }
    };

    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    };

    const retryConnection = async () => {
        setBackendStatus('checking');
        const result = await apiService.testBackend();
        setBackendStatus(result.connected ? 'connected' : 'disconnected');
    };

    return (
        <div className="h-screen bg-background flex">
            <div className="flex-1 flex items-center justify-center p-8 overflow-y-auto">
                <div className="max-w-md w-full">
                    <div className="flex items-center justify-start mb-16">
                        <img src={logo} alt="Equilibrio" className="h-12 mr-4" />
                        <h1 className="text-2xl font-bold text-text">Equilibrio.</h1>
                    </div>

                    {/* Connection Status */}
                    {backendStatus === 'checking' && (
                        <div className="mb-4 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
                            <p className="text-yellow-700 text-sm flex items-center">
                                <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-yellow-600" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                </svg>
                                Testing backend connection... (check browser console)
                            </p>
                        </div>
                    )}

                    {backendStatus === 'disconnected' && (
                        <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded-lg">
                            <p className="text-red-700 text-sm mb-2">
                                ❌ Backend connection issues detected
                            </p>
                            <ul className="text-red-600 text-xs list-disc list-inside mb-2">
                                <li>Check if backend is running on http://127.0.0.1:8000</li>
                                <li>Open browser Developer Tools (F12) and check Console tab</li>
                                <li>Verify the proxy is working in package.json</li>
                            </ul>
                            <button
                                onClick={retryConnection}
                                className="text-red-700 text-sm underline hover:no-underline"
                            >
                                Retry connection test
                            </button>
                        </div>
                    )}

                    <div className="mt-8">
                        <h1 className="text-3xl font-bold text-text text-left mb-2">
                            Welcome back!
                        </h1>
                        <p className="text-metallic-gray text-left mb-8">
                            Please enter your details to log in
                        </p>

                        {errors.general && (
                            <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-lg">
                                <p className="text-red-600 text-sm">{errors.general}</p>
                            </div>
                        )}

                        <form onSubmit={handleSubmit}>
                            <div className="mb-6">
                                <label className="block text-text font-medium mb-3 text-left">
                                    Email
                                </label>
                                <input
                                    type="email"
                                    name="email"
                                    value={formData.email}
                                    onChange={handleChange}
                                    placeholder="Enter your email"
                                    className={`w-full p-4 border rounded-lg bg-white placeholder-placeholder focus:outline-none focus:ring-2 focus:ring-blue focus:border-transparent ${
                                        errors.email || errors.username ? 'border-red-500' : 'border-strokes'
                                    }`}
                                    required
                                />
                                {(errors.email || errors.username) && (
                                    <p className="text-red-500 text-sm mt-1 text-left">
                                        {errors.email || errors.username}
                                    </p>
                                )}
                            </div>

                            <div className="mb-6">
                                <label className="block text-text font-medium mb-3 text-left">
                                    Password
                                </label>
                                <div className="relative">
                                    <input
                                        type={showPassword ? "text" : "password"}
                                        name="password"
                                        value={formData.password}
                                        onChange={handleChange}
                                        placeholder="*********"
                                        className={`w-full p-4 pr-12 border rounded-lg bg-white placeholder-placeholder focus:outline-none focus:ring-2 focus:ring-blue focus:border-transparent ${
                                            errors.password ? 'border-red-500' : 'border-strokes'
                                        }`}
                                        required
                                    />
                                    <button
                                        type="button"
                                        className="absolute inset-y-0 right-0 pr-3 flex items-center"
                                        onClick={togglePasswordVisibility}
                                    >
                                        {showPassword ? (
                                            <svg className="h-5 w-5 text-metallic-gray" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                                            </svg>
                                        ) : (
                                            <svg className="h-5 w-5 text-metallic-gray" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13.875 18.825A10.05 10.05 0 0112 19c-4.478 0-8.268-2.943-9.543-7a9.97 9.97 0 011.563-3.029m5.858.908a3 3 0 114.243 4.243M9.878 9.878l4.242 4.242M9.88 9.88l-3.29-3.29m7.532 7.532l3.29 3.29M3 3l3.59 3.59m0 0A9.953 9.953 0 0112 5c4.478 0 8.268 2.943 9.543 7a10.025 10.025 0 01-4.132 5.411m0 0L21 21" />
                                            </svg>
                                        )}
                                    </button>
                                </div>
                                {errors.password && (
                                    <p className="text-red-500 text-sm mt-1 text-left">{errors.password}</p>
                                )}
                            </div>

                            <div className="flex items-center justify-between mb-6">
                                <label className="flex items-center">
                                    <input
                                        type="checkbox"
                                        name="rememberMe"
                                        checked={formData.rememberMe}
                                        onChange={handleChange}
                                        className="h-4 w-4 text-blue focus:ring-blue border-strokes rounded"
                                    />
                                    <span className="ml-2 text-text text-sm">Remember sign in</span>
                                </label>
                                <a href="#" className="text-blue text-sm hover:text-blue-600">
                                    Forgot password?
                                </a>
                            </div>

                            <Button
                                type="submit"
                                variant="primary"
                                disabled={isLoading}
                                className="w-full"
                            >
                                {isLoading ? (
                                    <span className="flex items-center justify-center">
                                        <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                        </svg>
                                        Signing in...
                                    </span>
                                ) : (
                                    'Sign in'
                                )}
                            </Button>
                        </form>

                        <div className="text-center mt-6">
                            <p className="text-metallic-gray">
                                Don't have an account?{' '}
                                <a href="/signup" className="text-blue font-medium hover:text-blue-600">
                                    Sign up
                                </a>
                            </p>
                        </div>
                    </div>
                </div>
            </div>

            <div className="flex-1 hidden lg:block relative">
                <img
                    src={clockimage}
                    alt="Finance Analytics"
                    className="absolute inset-0 w-full h-full object-cover"
                />
            </div>
        </div>
    );
};

export default Login;
