// src/components/SignUp.js
import React, { useState, useEffect } from 'react';
import Button from '../ui/Button';
import logo from '../../assets/images/logo.png';
import clockimage from '../../assets/images/clock-image.png';
import { apiService } from '../../services/api';

const SignUp = () => {
    const [formData, setFormData] = useState({
        fullName: '',
        email: '',
        password: ''
    });
    const [showPassword, setShowPassword] = useState(false);
    const [errors, setErrors] = useState({});
    const [isLoading, setIsLoading] = useState(false);
    const [successMessage, setSuccessMessage] = useState('');
    const [backendStatus, setBackendStatus] = useState('checking');

    // Test backend connection on component mount
    useEffect(() => {
        const checkBackend = async () => {
            const isConnected = await apiService.testConnection();
            setBackendStatus(isConnected ? 'connected' : 'disconnected');
        };
        checkBackend();
    }, []);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
        // Clear errors when user starts typing
        if (errors[e.target.name]) {
            setErrors({
                ...errors,
                [e.target.name]: ''
            });
        }
        if (errors.general) {
            setErrors({
                ...errors,
                general: ''
            });
        }
        if (successMessage) {
            setSuccessMessage('');
        }
    };

    const handleValidationErrors = (error) => {
        const validationErrors = {};

        if (error.errors && Array.isArray(error.errors)) {
            error.errors.forEach(err => {
                if (err.loc && err.msg) {
                    const field = err.loc[err.loc.length - 1];
                    // Map backend field names to frontend field names
                    const frontendField = field === 'full_name' ? 'fullName' : field;
                    validationErrors[frontendField] = err.msg;
                }
            });
        } else if (typeof error.errors === 'string') {
            validationErrors.general = error.errors;
        } else if (error.errors && typeof error.errors === 'object') {
            Object.keys(error.errors).forEach(key => {
                const frontendField = key === 'full_name' ? 'fullName' : key;
                validationErrors[frontendField] = error.errors[key];
            });
        }

        setErrors(validationErrors);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        setErrors({});
        setSuccessMessage('');

        // Basic frontend validation
        if (!formData.fullName || !formData.email || !formData.password) {
            setErrors({ general: 'Please fill in all fields' });
            setIsLoading(false);
            return;
        }

        try {
            const data = await apiService.register(formData.fullName, formData.email, formData.password);
            console.log('Sign up successful:', data);

            setSuccessMessage('Account created successfully! Redirecting to login...');

            // Redirect to login after successful registration
            setTimeout(() => {
                window.location.href = '/login';
            }, 2000);

        } catch (error) {
            console.error('Sign up error:', error);

            if (error.status === 0) {
                setErrors({ general: error.message });
                setBackendStatus('disconnected');
            } else if (error.status === 422) {
                handleValidationErrors(error);
            } else if (error.status === 400) {
                setErrors({ general: 'User with this email already exists' });
            } else {
                setErrors({ general: error.message || 'An error occurred during registration' });
            }
        } finally {
            setIsLoading(false);
        }
    };

    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    };

    const retryConnection = async () => {
        setBackendStatus('checking');
        const isConnected = await apiService.testConnection();
        setBackendStatus(isConnected ? 'connected' : 'disconnected');
    };

    return (
        <div className="h-screen bg-background flex">
            {/* Left side - Form (without card style) */}
            <div className="flex-1 flex items-center justify-center p-8 overflow-y-auto">
                <div className="max-w-md w-full">
                    {/* Logo and Title - At the top */}
                    <div className="flex items-center justify-start mb-16">
                        <img
                            src={logo}
                            alt="Equilibrio"
                            className="h-12 mr-4"
                        />
                        <h1 className="text-2xl font-bold text-text">Equilibrio.</h1>
                    </div>

                    {/* Backend Status Indicator */}
                    {backendStatus === 'checking' && (
                        <div className="mb-4 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
                            <p className="text-yellow-700 text-sm flex items-center">
                                <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-yellow-600" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                </svg>
                                Checking backend connection...
                            </p>
                        </div>
                    )}

                    {backendStatus === 'disconnected' && (
                        <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded-lg">
                            <p className="text-red-700 text-sm mb-2">
                                ❌ Cannot connect to backend server. Please ensure:
                            </p>
                            <ul className="text-red-600 text-xs list-disc list-inside mb-2">
                                <li>Backend is running on http://127.0.0.1:8000</li>
                                <li>CORS is enabled in your FastAPI backend</li>
                            </ul>
                            <button
                                onClick={retryConnection}
                                className="text-red-700 text-sm underline hover:no-underline"
                            >
                                Retry connection
                            </button>
                        </div>
                    )}

                    {/* Form without card styling */}
                    <div className="mt-8">
                        <h1 className="text-3xl font-bold text-text text-left mb-2">
                            Create an account
                        </h1>
                        <p className="text-metallic-gray text-left mb-8">
                            Please enter your details to register
                        </p>

                        {/* Success message */}
                        {successMessage && (
                            <div className="mb-6 p-4 bg-green-50 border border-green-200 rounded-lg">
                                <p className="text-green-600 text-sm">{successMessage}</p>
                            </div>
                        )}

                        {/* General error message */}
                        {errors.general && (
                            <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-lg">
                                <p className="text-red-600 text-sm">{errors.general}</p>
                            </div>
                        )}

                        <form onSubmit={handleSubmit}>
                            {/* Full Name */}
                            <div className="mb-6">
                                <label className="block text-text font-medium mb-3 text-left">
                                    Full Name
                                </label>
                                <input
                                    type="text"
                                    name="fullName"
                                    value={formData.fullName}
                                    onChange={handleChange}
                                    placeholder="Full name"
                                    className={`w-full p-4 border rounded-lg bg-white placeholder-placeholder focus:outline-none focus:ring-2 focus:ring-blue focus:border-transparent ${
                                        errors.fullName ? 'border-red-500' : 'border-strokes'
                                    }`}
                                    required
                                    disabled={backendStatus === 'disconnected'}
                                />
                                {errors.fullName && (
                                    <p className="text-red-500 text-sm mt-1 text-left">{errors.fullName}</p>
                                )}
                            </div>

                            {/* Email */}
                            <div className="mb-6">
                                <label className="block text-text font-medium mb-3 text-left">
                                    Email
                                </label>
                                <input
                                    type="email"
                                    name="email"
                                    value={formData.email}
                                    onChange={handleChange}
                                    placeholder="example@gmail.com"
                                    className={`w-full p-4 border rounded-lg bg-white placeholder-placeholder focus:outline-none focus:ring-2 focus:ring-blue focus:border-transparent ${
                                        errors.email ? 'border-red-500' : 'border-strokes'
                                    }`}
                                    required
                                    disabled={backendStatus === 'disconnected'}
                                />
                                {errors.email && (
                                    <p className="text-red-500 text-sm mt-1 text-left">{errors.email}</p>
                                )}
                            </div>

                            {/* Password */}
                            <div className="mb-6">
                                <label className="block text-text font-medium mb-3 text-left">
                                    Password
                                </label>
                                <div className="relative">
                                    <input
                                        type={showPassword ? "text" : "password"}
                                        name="password"
                                        value={formData.password}
                                        onChange={handleChange}
                                        placeholder="••••••••"
                                        className={`w-full p-4 pr-12 border rounded-lg bg-white placeholder-placeholder focus:outline-none focus:ring-2 focus:ring-blue focus:border-transparent ${
                                            errors.password ? 'border-red-500' : 'border-strokes'
                                        }`}
                                        required
                                        disabled={backendStatus === 'disconnected'}
                                    />
                                    <button
                                        type="button"
                                        className="absolute inset-y-0 right-0 pr-3 flex items-center"
                                        onClick={togglePasswordVisibility}
                                        disabled={backendStatus === 'disconnected'}
                                    >
                                        {showPassword ? (
                                            <svg className="h-5 w-5 text-metallic-gray" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                                            </svg>
                                        ) : (
                                            <svg className="h-5 w-5 text-metallic-gray" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13.875 18.825A10.05 10.05 0 0112 19c-4.478 0-8.268-2.943-9.543-7a9.97 9.97 0 011.563-3.029m5.858.908a3 3 0 114.243 4.243M9.878 9.878l4.242 4.242M9.88 9.88l-3.29-3.29m7.532 7.532l3.29 3.29M3 3l3.59 3.59m0 0A9.953 9.953 0 0112 5c4.478 0 8.268 2.943 9.543 7a10.025 10.025 0 01-4.132 5.411m0 0L21 21" />
                                            </svg>
                                        )}
                                    </button>
                                </div>
                                {errors.password && (
                                    <p className="text-red-500 text-sm mt-1 text-left">{errors.password}</p>
                                )}
                                <p className="text-xs text-metallic-gray mt-2 text-left">
                                    Use at least one special character #$%@
                                </p>
                            </div>

                            {/* Divider */}
                            <div className="relative my-6">
                                <div className="absolute inset-0 flex items-center">
                                    <div className="w-full border-t border-strokes"></div>
                                </div>
                            </div>

                            {/* Submit Button */}
                            <Button
                                type="submit"
                                variant="primary"
                                disabled={isLoading || backendStatus === 'disconnected'}
                                className="w-full"
                            >
                                {isLoading ? (
                                    <span className="flex items-center justify-center">
                                        <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                        </svg>
                                        Creating Account...
                                    </span>
                                ) : backendStatus === 'disconnected' ? (
                                    'Backend Unavailable'
                                ) : (
                                    'Create Account'
                                )}
                            </Button>
                        </form>

                        {/* Footer */}
                        <div className="text-center mt-6">
                            <p className="text-metallic-gray">
                                Already have an account?{' '}
                                <a href="/login" className="text-blue font-medium hover:text-blue-600">
                                    Sign in
                                </a>
                            </p>
                        </div>
                    </div>
                </div>
            </div>

            {/* Right side - Image without scroll */}
            <div className="flex-1 hidden lg:block relative">
                <img
                    src={clockimage}
                    alt="Finance Analytics"
                    className="absolute inset-0 w-full h-full object-cover"
                />
            </div>
        </div>
    );
};

export default SignUp;
